ruleToc.chapterList:
@js:
let v = [],
		list = [];
JSON.parse(result).list.map($=>{

//分卷判定
		V = $.name;
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: '📖['+V+']📖',
						volume: true
					})
			}

$.bookChapters.map($=>{
		return list.push({
				name: $.name,
				url: `https://readbook-service-freebook.cread.com/cx/itf/chapterRead?bookId=${$.bookid}&chapterId=${$.id}`,
				info: `章节字数：${$.wordCount}　更新时间：${$.updateDate}`
			});
	});
});
v.length<2?list.filter($=>!$.volume):list

ruleToc.nextTocUrl:
@js:
list=[];
if(!(baseUrl.match(/series/)||baseUrl.match(/ebook/)||baseUrl.match(/comments/))){
if(baseUrl.match(/author/)){
if(result.match(/class="count">\(共(\d+)条\)/)){
count=result.match(/class="count">\(共(\d+)条\)/)[1];
page=parseInt(count/10);
for(i=0;i<page;i++){
list.push(baseUrl+'&start='+(i*10))
}}}else{
if(result.match(/data-total-page="([^"]+)">/)){
p=result.match(/data-total-page="([^"]+)">/)[1];
page=20;
if(p<page){
page=p
}
for(i=1;i<=page;i++){
list.push(baseUrl+'?start='+((i-1)*20))
}}}}
list

