var list=[];
var url=result.match(/\/c\/\d+\//g);
var n1=new RegExp(/mulu">(.+)<\/d/g);
var n3=new RegExp(/href="\/c\/\d+\/"><li>(.+?)<\/l/g);
var n;var n_pre='';var n_sub;
var n_temp;
var t=-1;
var p=[];var p_tmp;
var i=-1;var i_tmp;
//获取每卷的标题
while((n_temp=n1.exec(result))!=null){
i=i+1;
p.push({q:n_temp[1],w:n1.lastIndex});
}
i_tmp=i;

while((n_temp=n3.exec(result))!=null){
//获取每话的标题
n_sub=n_temp[1];
//判断VIP
if((n_sub.match(/span/))!=null){
n_sub=n_sub.match(/span>(.+)<\/span><spa/)[1];n_sub='🔒'+n_sub;
}
//给每话的标题添加卷标题
if(p[0]!=null){
i=i_tmp;
p_tmp=p[i].w;
while(p_tmp>n3.lastIndex){
i=i-1;
p_tmp=p[i].w;
}
n_pre=p[i].q;
n_pre=n_pre+' | ';
}
//保存数据到数组
n=n_pre+n_sub;
t=t+1;
list.push({k:n,v:url[t]});
}
list;

