function loginqt(){
    let key = gets_key()
    if (key && key != "") {
            java.toast("您已填写秘钥:"+key);
            return false
        }
    java.startBrowserAwait(getArgument('server')+'/check_key','登录晴天小说书源');
    key = gets_key()
    if (key){
        java.longToast('登录成功，秘钥：'+key);
        return true
        } else {
            java.toast('登录失败！！！！！');
            return false
            }
}

