};
var opts = {
  showVolName: false
};

function chapterLevel(rules, opts){
  //return Array
  //volsName: å·æ ‡é¢˜è§„åˆ™ï¼ˆXPathï¼‰
  //volumes: å·è§„åˆ™
  //volInfo(' '): å·ç®€ä»‹è§„åˆ™ï¼ˆvolumeså­è§„åˆ™ï¼‰
  //chapters: ç« èŠ‚è§„åˆ™ï¼ˆvolumeså­è§„åˆ™ï¼‰
  //chName: ç« èŠ‚æ ‡é¢˜è§„åˆ™ï¼ˆchapterså­è§„åˆ™ï¼‰
  //chLink: ç« èŠ‚urlè§„åˆ™ï¼ˆchapterså­è§„åˆ™ï¼‰
  //vip(' '): åˆ¤å®švipçš„è§„åˆ™ï¼ˆchapterså­è§„åˆ™
  //showVolName(false): æ˜¯å¦åœ¨ç« èŠ‚åç§°å‰æ˜¾ç¤ºå·å

  var check = (rule) => rule !== undefined && rule !== '' ? rule : '//x[@x="" and @x!=""]';
  rules.volInfo = check(rules.volInfo);
  rules.vip = check(rules.vip);
  opts.showVolName = opts.showVolName === undefined ? true : !!opts.showVolName;

  var NL = '<br/>', NP = "<br/>\uFEFF<br/>";
  var list = [];
  var volsName = java.getStringList(rules.volsName).toArray();

  java.getElements(rules.volumes)
    .toArray().forEach((vol,idx) => {
    java.setContent(vol);
    var chs = java.getElements(rules.chapters)
    .toArray().map(ch => {
      java.setContent(ch);
      var isVip = 0;
      var chName = java.getString(rules.chName,false);
      return {
        text:(isVip ? 'ğŸ’°' : '').padStart(4, "\u2000")+(opts.showVolName ? volsName[idx]+"\x20" : '')+chName,    href:'http://www.ebtang.com/book/readbook/'+bookid+'/'+java.getString(rules.chLink,false),
        name: chName
      };
    });

    var volInfo = 'ç®€ä»‹ï¼š' + NL + java.getStringList(rules.volInfo)
      .toArray().join(NL);
    volInfo += NP + 'ç›®å½•(' + chs.length + ')ï¼š' + NL;
    volInfo += chs.slice(0,10).map(v => v.name).join(NL);
    volInfo += chs.length > 10 ? NL + 'â€¦â€¦' : '';
    volInfo = volInfo.replace(/(\s)\1+/g, '$1');
    list.push({
      text: 'ğŸ”»[ '+volsName[idx]+' ]',
      href: 'https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+encodeURIComponent(volInfo)
    });
    list = list.concat(chs);
  });

  return list;
}

chapterLevel(rules, opts);
