]
let titleStyle = {
    layout_flexGrow: 0,
    layout_flexShrink: 1,
    layout_alignSelf: "auto",
    layout_flexBasisPercent: 1,
    layout_wrapBefore: false
};
let defaultStyle = {
    layout_flexBasisPercent: .28
};
let result = [];
for (let pre of prefix) {
    let title = `🔹 ${pre.n} 🔹`;
    result.push({
        title: title,
        url: "",
        style: titleStyle
    });
    for (let t of types) {
        result.push({
            title: t.n,
            url: `/${pre.p}${t.p}${pre.s}{{page}}.html`,
            style: defaultStyle
        });
    }
}

JSON.stringify(result);

