};
n2s = (num) => {
    if (num > 10000) {
        return `${(num / 10000).toFixed(1)}万`;
    } else {
        return String(num);
    }
};

//单曲
if (info.type == "song") {
    data = JSON.stringify({
        "req_0": {
            "module": "music.trackInfo.UniformRuleCtrl",
            "method": "CgiGetTrackInfo",
            "param": {
                "ids": [info.id], "types": [0]
            }
        },
        "comm": {
            "g_tk": 5381, "uin": 0, "format": "json", "platform": "h5", "deviceToken": "", "deviceId": "", "cv": 0
        }
    });
    songs = zzcSignPost(data).req_0.data.tracks;
    tracks = songs.map(song => ({
