ruleBookInfo.init:
@js:
eval(String(source.variableComment));
var Csrf_token = cookie.getKey(source.getKey(), '__csrf');
var songlist = [];

function fetchData(param, url) {
    var params = encryption(encryption(JSON.stringify(param), '0CoJUm6Qyw8W8jud'), i_data);
    var encSecKey = encSecKeyEncryption(i_data, encSecKeyParameterOne, encSecKeyParameterTwo);
    var body = `params=${params}&encSecKey=${encSecKey}`;
    var headers = {
        'content-type': 'application/x-www-form-urlencoded',
        'referer': 'https://music.163.com/'
    };
    var option = {
        "method": "POST",
        "body": body,
        "headers": headers
    };
    var postUrl = url + "," + JSON.stringify(option);
    var resp = JSON.parse(java.ajax(postUrl));
    return resp;
}

function formatDuration(milliseconds) {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
}

//单曲
if(baseUrl.includes("song?id=")){
var songId = baseUrl.match(/song\?id=(\d+)/)[1];
var param = {c: `[{"id":${songId}}]`,ids: `[${songId}]`};
var resp = fetchData(param, 'https://music.163.com/weapi/v3/song/detail');
java.put("tracks", JSON.stringify(resp.songs));
var songlist = resp.songs[0];

var param = {
    rid: `R_SO_4_${songId}`,
    threadId: `R_SO_4_${songId}`,
    pageNo: 1,
    pageSize: 20,
    cursor: -1,
    offset: 0,
    orderType: 1
};
var commResp = fetchData(param, `https://music.163.com/weapi/comment/resource/comments/get?csrf_token=${Csrf_token}`);
var comments = commResp.data.hotComments;

if(comments.length){
var formattedComments = comments.map(comment => {
	return `👤${comment.user.nickname}:【${comment.content} 】\n(${comment.timeStr})           👍(${comment.likedCount})  🗨️(${comment.replyCount})`;
}).join('\n.\n');
}
else{
var formattedComments = '暂时还没有热评※=○☆(＿＿*)Ｚｚｚ';
}

var info = `&nbsp;💿 歌曲：${songlist.name}\n🎤 歌手：${songlist.ar.map(artist => artist.name).join(' ')}\n🏷 发行：${String(java.timeFormat(songlist.publishTime)).slice(0,10)}\n⏰ 时长：${formatDuration(songlist.dt)}\n🏆 专辑：${songlist.al.name}\n.\n————————热评————————\n.\n${formattedComments}`;
result = JSON.stringify({
'name': songlist.name,
'author': songlist.ar.name,
'kind': songlist.al.name,
'info': info,
'cover': songlist.al.picUrl,
'id': songId
})
}

//我的听歌排行
else if (baseUrl.includes("songs/rank")){
var userInfo = JSON.parse(java.get("userinfo"));
