        return;
    } else if (Password == '') {
        login('【登录提示】' + '\n\n请输入密码');
        return;
    } else {
        var requestBody = 'loginName=' + Account + '&password=' + Password;
        var headers = {"Content-Type":"application/x-www-form-urlencoded"};
        var option = {
            "method": "POST",
            "body": String(requestBody),
            "headers": headers
        };
        Response = java.ajax(Url("源站：") + '/login,' + JSON.stringify(option));
        if (JSON.parse(Response).model) {
            cache.put("uaaAccount",Account);
            cache.put("uaaToken",JSON.parse(Response).model.token);
            cache.put("uaaCookie",cookie.getCookie(Url("源站：")));
            login('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n账号：' + Account + '\n┋┋' + '\n❤️登录成功❤️\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛');
        } else {
            cache.put("uaaAccount","");
            cache.put("uaaToken","");
            cache.put("uaaCookie","");
            cookie.removeCookie(Url("源站："));
            login('【登录提示】' + '\n\n♣️登录失败♣️\n\n请检查账号和密码');
