searchUrl:
@js:
var su=source.key,
so=su+"/search/,";
body="searchkey="+key;
var option={
  "body": String(body),
  "method": "POST",
  "webView": true
};
var head=JSON.parse(source.header);
option.headers=head;
var post=JSON.stringify(option);
java.put("url",String(so+post))

