@js:
try{
data=java.getString("h1@text");
//java.log(data)
ys=data.match(/\d+\/(\d+).?$/)||[];
page=ys[1];
var next=[];
if(data&&page){
	for(i=2;i<=page;i++){
		url=baseUrl.replace(/.html/,
			`_${i}.html`);
		next.push(url);
		}
		next;
	}
}catch(e){
	java.log(e)
	}finally{next}

