@js:// 获取源代码中的Token拼接到搜索地址
var url = source.getKey();
var header = JSON.parse(source.header);
html=java.ajax(url);
token=html.match(/name="_token" value="(.+?)"/);
token=token?token[1]:"";
java.log(token);
var body = "keyword="+key+"&_token="+token;
var option = { 
	"charset": "utf-8",
	"method": "POST",
