ruleSearch.lastChapter:
@js:
last=JSON.parse(java.ajax('http://android.jjwxc.net/androidapi/chapterList?novelId={{$.novelid||$.novelId}}')).chapterlist;
if(last){
last=last[last.length-1];
vip=last.isvip!=0?"🔒":'';
date=last.chapterdate;
chapter=last.chaptername;
chapterid=last.chapterid;
result=vip+chapterid+'.'+chapter+'•'+date}

ruleToc.chapterName:
$.chaptername
@js:
result=result.replace(/^\s+/,'');
intro=java.getString("$.chapterintro");
java.put("intro",intro);
vip='{{$.isvip}}'!='0';
lock='{{$.islock}}'!='0';
type='{{$.chaptertype}}'=='1';
num=!type?'{{$.chapterid}}、':'';
if(result.match(/[一二三四五六七八九十百千万\d]+\s*章|^\d+[、\.\s]|chapter\s*\d+/i)){
num=''
}else{num=num}
juan=type?'📖 '+result+' 📖':result;
v=vip&&!type?'🔒 ':'';l=lock?'[此章节已锁]':'';
result=v+num+juan+l;

