loginCheckJs:
nologinflag = /<div\s*class="title"\s*>.*1234.*/.test(result.body());
//提取当前加载的域名
_baseUrl = String(result.url()).replace(/(https?:\/\/.*?\/).*/, "$1");

try {
	data = JSON.parse(source.getVariable());
} catch (err) {
	data = {};
}

reSearch = false;
if (baseUrl != _baseUrl&&data.url != _baseUrl) {
		reSearch = true;
		data.url = _baseUrl;
		source.setVariable(JSON.stringify(data));
		java.initUrl();
}
url = data.url || _baseUrl;
if (nologinflag) {
	url = String(url + "," + JSON.stringify({
		"method": "POST",
		"body": "action=1&v=1234"
	}));
	loginres = java.ajax(url);
	if (loginres == "success") nologinflag = loginres;
	result = java.getStrResponse();
} else if (reSearch && result.url().indexOf("s.php") != -1) {
	//搜索时 重定向新网址需要重新获取搜索内容
	result = java.getStrResponse();
}
result

