<script>
function pp(a){
	b=a.replace(/\\/,'');
$("#textReplace").value+=b;
$("#textReplace").focus()
}
function dis(){
document.getElementById("bt1").disabled=true;
document.getElementById("bt2").disabled=true
}
function di(){
