<js>
// 更新状态
a = "{{$..vodRemarks}}";
b = "{{$..vodTotal}}";
if(!a.match(/完结/)&&!b.match(/^0*$/))a+="(共"+b+"集)";
gx = a+"{{$..vodPubdate##(.+)## ∙ $1}}";


// 格式化解析
jiexi = eval(String(source.sourceComment)).map($=>'<button onclick="xuan(this,\'.jiexi\')" value="'+$[1]+'"><b>'+$[0]+'</b></button>');


// 格式化集数
jishu = result.split('#').map($=>{
		$ = $.split('$');
		return '<button onclick="xuan(this,\'.jishu\')" value="'+$[1]+'"><b>'+$[0]+'</b></button>'
	});



// 返回json
JSON.stringify({
	gx: gx,
	jiexi: '<div>\n\t\t'+jiexi.join('\n\t\t')+'\n</div>',
	jishu: '<div>\n\t\t'+jishu.join('\n\t\t')+'\n</div>'
});
</js>
