<script>
//选中标签
$=(rule)=>document.querySelectorAll(rule);

//删除active
omit=(A)=>Array.from(A,a=>a.className='');

//增加active
active=($,i)=>$[i].className='active';

//选择集数
xuan = (item,type)=>{
		btn = $(type+' button.active');

		if(item.value==btn[0].value)return;
		omit(btn); item.className='active'
		return geturl()
	}


//拼接链接
val=(type)=>$(type+' button.active')[0].value.trim();
geturl=()=>
	$('iframe')[0].src=$('p')[0].innerText=
		val('.jiexi') + val('.jishu');


//初始化
(()=>{
		active($('.jiexi button'),0)
		active($('.jishu button'),0)
		return geturl()
	})()
</script>## ∙ 0+(\s*)##$1

