].map($=>'<button value="'+$[1]+'"><b>'+$[0]+'</b></button>');


i = 0;
x = 1;
jishu = [];
jiekou = [];
document.body.innerText.split('\n')
.map($=>{
		$ = $.split(',');
if(  $[1]!=undefined && !$[1].match(/\[.+\]/)  ){
		if($[1].match(/^#.+#$/)){
			if(!black1.test($[0])){
				x=1;
				jiekou.push('<button value="'+i+'"><b>'+$[0]+'</b></button>');
				jishu.push([]);
				i++;
			}
			else{
				x=0;
			}
			}
		else{
			if( x && !black2.test($[1])  ){
				jishu[jishu.length-1].push('<button value="'+$[1]+'"><b>'+$[0]+'</b></button>');
				}
			}
}
	});



jiexi = '<div>\n\t\t'+jiexi.join('\n\t\t')+'\n</div>';
jiekou = '<div>\n\t\t'+jiekou.join('\n\t\t')+'\n</div>';
jishu = jishu.map($=>'<div>\n\t\t'+$.join('\n\t\t')+'\n</div>').join('\n');


