	overflow-x:auto;
	overflow-y:hidden;
	white-space:nowrap;
}
.jishu>div.active{
	display:block;
}

button{
	width:21.65%;
	margin:1.25%;
	padding:5px;
	outline:none;
	border-radius:8px;
	font-size:0.7rem;
