ruleSearch.bookList:
.t_l
<js>
/*
result=source.getVariable()
result=org.jsoup.Jsoup.parse(result)
result=result.select(".t_l")
java.logType(result)
java.log(result.length)
java.log(result)
*/
	try{
		v=source.getVariable()
		Variable=JSON.parse(v)
		booknameList=Variable.booknameList
		bookList=Variable.bookList
		}catch(e){
			booknameList=[]
			bookList=[]
			}

result.forEach(b=>{
	//java.logType(b)
	//java.log(b)
	name=java.getString(".t_subject@text",b)
	uploader=java.getString(".t_author@text",b)
	bookname=name.match(/【[^】]+】/)
	time=String(java.getString(".t_dateline@text",b)).split('/')
	time.unshift(time.pop())
	time=time.join('/')
	if(bookname){
		if (booknameList.indexOf(String(bookname))==-1){
			bookname=String(bookname)
			booknameList.push(String(bookname))
			author=name.split('作者：')[1]
			book={bookname:bookname,author:author,time:time,uploader:uploader,sum:1}
			bookList.push(book)
			//java.log(JSON.stringify(book))
			}else{
				bookList[booknameList.indexOf(String(bookname))].sum+=1
				}
		}else{
			book={bookname:name,time:time,uploader:uploader,sum:1}
			bookList.push(book)

			}	
	})
	source.setVariable(JSON.stringify({bookList:bookList,booknameList:booknameList}))
	result=(JSON.stringify(bookList))
</js>
$[*]

ruleToc.chapterList:
-.t_l
<js>
let chapters = result
let chapterData = [];
let invalidChapterFound = false;

chapters.forEach((item) => {
    let title = item.select(".t_subject").text();
    let href = item.select("a")[0].attr("href");
    let chapterNumberMatch = title.match(/（([^\)]+)）/);
    if (!chapterNumberMatch) {
        // 检测到无效章节，标记为不合法
        invalidChapterFound = true;
    } else {
        let chapterNumber = parseFloat(chapterNumberMatch[1]);
        chapterData.push({
            number: chapterNumber,
            text: title,
            href: href
        });
     }
});
java.log(invalidChapterFound)
if (book.getVariable("custom")!=""&&invalidChapterFound==false) {
	if (baseUrl==book.bookUrl){
		java.toast("目录乱序修复已开启")
		}
    chapterData.sort((a, b) => b.number - a.number);
} else {
    chapterData = []
    chapters.forEach((item) => (chapterData.push({
        text: item.select(".t_subject").text(),
        href: item.select("a")[0].attr("href")
    })));
}
java.log(JSON.stringify(chapterData))
chapterData
</js>

