            const numA = parseInt(a.attr("id").match(/\d+$/), 10) || 0;
            const numB = parseInt(b.attr("id").match(/\d+$/), 10) || 0;
            return numA - numB;
        });

        // 将排序后的元素内容拼接成字符串
