function getl(arr, page) {
	return arr.slice(page * 500, (+page + 1) * 500)
}

function toDataUrl(key, page, sortType) {
	let data = [key, page, sortType]
	return `data:;base64,${this.java.base64Encode(data.join("|==|"))},{"type":""}`
}

function sleep(t) {
	Packages.java.lang.Thread.sleep(t)
}

function createRegExp(input) {
	const flagsRegex = /\(\?([igm]*)\)(?!\))/g;
	let flags = '';
	let pattern = input;
	let match;
	while ((match = flagsRegex.exec(input)) !== null) {
		flags += match[1];
		pattern = pattern.substring(0, match.index) + pattern.substring(match.index + match[0].length);
	}
	return new RegExp(pattern, flags);
}

function getSortValue(book, type) {
	let data = book[type]
	let value
	switch (type) {
		case 'pornrate':
			value = data.split("%")[0]
			break;
		case 'update':
			value = parseInt(data.replace(/-/g, ""))
			break;
		case 'word':
			m = data.match(/[^\d.]+/)
			f = 1
			if (m && /k/i.test(m)) {
				f = 1000
			} else if (m && /m/i.test(m)) {
				f = 1000000
			}
			value = Number(data.match(/[\d.]+/)) * f
			break;
		default:
			value = data
	}
	return value
}

function spArr(arr, num) {
	let newArr = []
	for (let i = 0; i < arr.length;) {
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}

var okhttpImport = new JavaImporter();
okhttpImport.importPackage(
	Packages.okhttp3,
	Packages.java.lang,
	Packages.java.io,
	Packages.java.util.zip,
	Packages.cn.hutool.core.io,
	Packages.io.legado.app.model.analyzeRule
);
with(okhttpImport) {
	client = new OkHttpClient();
	function postMultipart(url, data, type, filename, _) {
		const { source } = _ || this;
		var mediaType = MediaType.parse(type);
		var requestBody = RequestBody.create(data, mediaType);
		var multipartBody = new MultipartBody.Builder()
			.setType(MultipartBody.FORM)
			.addFormDataPart("file", filename, requestBody)
			.build();
		var request = new Request.Builder()
			.url(url)
			.post(multipartBody)
			.addHeader("User-Agent", source.getHeaderMap(false)["User-Agent"])
			.build();
		var response = client.newCall(request).execute();
		try {
			// å¤„ç†å“åº”
			var responseBody = response.body().string();
			return responseBody;
		} finally {
			response.close();
		}
	}

	function upload(data, rule, filename, _) {
		const { java } = _ || this;
		filename = filename || "bookSource.json";
		type = "application/json; charset=utf-8";
		if (rule.compress) {
			bytesdata = new Packages.java.lang.String(data).getBytes("UTF-8");
			type = "application/zip; charset=utf-8";
			var byteOut = new ByteArrayOutputStream();
			let zipOut = new ZipOutputStream(byteOut);
			let entry = new ZipEntry(filename);
			zipOut.putNextEntry(entry);
			zipOut.write(bytesdata);
			zipOut.closeEntry();
			zipOut.finish();
			filename += ".zip";
			data = byteOut.toByteArray();
		}
		let resp = postMultipart(rule.url, data, type, filename, _ || this);
		let analyzeRule=AnalyzeRule().setContent(resp, rule.url)
		return analyzeRule.getString(rule.downloadUrlRule)
	}
	function createFile(content,path) {
		return FileUtil.writeUtf8String(content,dataPath+path)
	}
	function readFile(path){
		return FileUtil.readUtf8String(dataPath+path)
	}
	function exist(path){
		return FileUtil.exist(dataPath+path)
	}
	function mkdir(path){
		FileUtil.mkdir(dataPath+path)
	}
}

function analyzeDirectLinkUploadRule(str){
	let rule=JSON.parse(str)
	rule.url=rule.uploadUrl.split(",")[0]
	downloadJs=rule.downloadUrlRule.replace(/@js:|<\/?js>/g,"\n")
	if (downloadJs.startsWith("$")){
		downloadJs=downloadJs.replace(/(\$.+)/,'result=Packages.com.jayway.jsonpath.JsonPath.read(resp,"$1")')
	} 
	rule.downloadJs=downloadJs
	return rule
}

