function cacheContent() {
	source.put('isEnd',0)
	$ = checkData()
	urls = []
	for (let i = $.lastestId; i > 0; i--) {
		if (!exist(`/book/${i}/index.html`)) {
			continue
		}
		result = readFile(`/book/${i}/index.html`)
		let e = org.jsoup.Jsoup.parse(result);
		let chapters = e.select(".book-chapter a")
		chapters.forEach(c => {
			curl = c.attr("href")
			if (!exist(curl)) {
				urls.push(sourceKey + curl)
			}
		})
		if (urls.length > $.sum) {
			break
		}
	}
	saveProgress(`\n🚀正在并发获取${urls.length}章节🚀`)
	sleep(1000)
	count = 0
	urlslist = spArr(urls, $.concurrency || 1)
	for (let i in urlslist) {
		if (Number(source.get('isEnd'))) {
			sleep(800)
			saveProgress(`\n❎用户终止在${count}/${urls.length}❎`)
			error = false
			sleep(800)
			break
		}
		try {
			htmlList = java.ajaxAll(urlslist[i])
			htmlList.forEach(h => {
				html = h.body()
				url = h.url().replace(sourceKey, "")
				let content = org.jsoup.Jsoup.parse(html).select(".content");
				if (content != "") {
					createFile(content, url)
					count++
					progress = `\n${count%2?"⏳":"⌛"}当前进度(${count}/${urls.length})\n${'﹦'.repeat(Math.floor((count/urls.length)*30))}${'﹣'.repeat(Math.ceil((urls.length-count)*30/urls.length))}`
					saveProgress(progress, true)
				}
			})
		} catch (e) {
			java.log(e)
			saveProgress(`\n❎终止在${count}/${urls.length}`)
			sleep(1000)
			break
		}
	}
	source.put("progress","")
	java.longToast(`\n✅缓存正文完成，新增${count}章节`)
}

