ruleToc.chapterList:
@js:
    //获取总页数
try{
        a = java.getString(".last.0@text").match(/\s(\d+)/)[1];
    }
catch {
    a = java.getElements(".pages.0@a").length;
    if(a) {java.log("有.pages，按长度")
    }
    else {java.log("没有.pages，取末页");
        aa = java.getString("text.末页@href");
        a = String(aa).replace(/.*page=/, "");
        java.log(a)
    }
}
list = [];
list.push({text: "第1页",href: baseUrl});
for(i = 2; i <= a; i++) {
    if(/thread-/.test(baseUrl)) {java.log("测thread");
        dz1 = baseUrl.replace(/-\d+-\d+.html/, "-");
        dz2 = baseUrl.match(/-\d+\.html$/);
        //	java.log(dz1+i+dz2);
        list.push({
            text: "第" + i + "页",
            href: dz1 + i + dz2
        })
    }
    else {java.log("page拼接")
        list.push({
            text: "第" + i + "页",
            href: baseUrl + "&page=" + i
        })
    }
}
java.log(JSON.stringify(list))
