手机品牌型号汇总
https://khwang9883.github.io/MobileModels/
安卓谷歌浏览器版本号
https://vikyd.github.io/download-chromium-history-version/json/ver-pos-os/version-position-Android.json
*/
function rMR(from, to) {
  from = parseInt(from);
  to = parseInt(to || 0);
  // 确保`from`小于等于`to`
  [from, to] = [Math.min(from, to), Math.max(from, to)];
  return Math.floor(Math.random() * (to - from + 1)) + from;
}

function randomUA() {
  let { java, cache } = this;
  // 谷歌浏览器版本号
  let chromeVersion;
  // android版本号 10-14
  let androidVersion = rMR(10, 14);
  try {
    let chromeVerList;
    if (cache.get("chromelist")) {
      chromeVerList = JSON.parse(cache.get("chromelist"));
    } else {
      // 获取谷歌浏览器版本号列表
      let chromeResult = java.ajax("https://vikyd.github.io/download-chromium-history-version/json/ver-pos-os/version-position-Android.json");
      chromeVerList = Object.keys(JSON.parse(chromeResult));
      cache.put("chromelist", JSON.stringify(chromeVerList), 86400);
    }
    chromeVersion = chromeVerList[rMR(0, 40)] || `119.0.6045.${rMR(190, 205)}`;
  } catch (error) {
    chromeVersion = `119.0.6045.${rMR(190, 205)}  `;
  }
  return `Mozilla/5.0 (Linux; Android ${androidVersion};) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/${chromeVersion} Mobile Safari/537.36`;
}

