    var datadist = {
        "0": "默认音色",
        "-1": "阅读模式",
        "-2": "漫画模式",
        "51": "多人发音",
        "1": "甜美少女",
        "2": "清亮青叔",
        "5": "开朗青年",
        "6": "温柔淑女",
        "4": "成熟大叔",
        "74": "大叔升级",
        "30": "优雅御姐"
    };
    var tone_id = datadist[arg] || arg;
    var tishi = '\n\n当前音色：' + tone_id;
    java.toast(tishi);
}

//设置音色
function set_tone_id(mode, name) {
    let zdytone_id;
    try {
        zdytone_id = String(source.getLoginInfoMap()['其他音色填写后点击右上角✔']);
        if (zdytone_id) {
            setArgument('tone_id', zdytone_id);
            java.toast(`\n\n当前音色为自定义音色\n${zdytone_id}\n\n切换音色请先清空音色输入框中的数据`);
        } else {
            zdytone_id = '';
        }
    } catch (error) {
        zdytone_id = '';
    }
    if (!zdytone_id) {
        let toast = "\n\n已切换至：" + name + '\n\n切换后需要刷新详情页';
        setArgument('tone_id', mode);
        java.toast(toast);
    }
}

