		}
	})
	return `${api}${t.uri},${options}`
}

function getUuid(e, t) {
	let n = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz".split(""),
		r = [],
		o;
	if (t = t || n.length, e)
		for (o = 0; o < e; o++) r[o] = n[0 | Math.random() * t];
	else {
		let s;
		for (r[8] = r[13] = r[18] = r[23] = "-", r[14] = "4", o = 0; o < 36; o++) r[o] || (s = 0 | Math.random() * 16, r[o] = n[o == 19 ? s & 3 | 8 : s])
	}
	return r.join("")
}

function getDatekey() {
	let t = new Date;
	t.setHours(0, 0, 0, 0);
	let r = t.getTime().toString();
	return this.java.digestBase64Str(r,"SHA256")
}

// rc4åŠ å¯†å®ç°
function rc4(key, str) {
	let s = new Array(256);
	let j = 0, i, x;
	let res = new Packages.java.io.ByteArrayOutputStream();
	for (i = 0; i < 256; i++) s[i] = i;
	for (i = 0; i < 256; i++) {
		j = (j + s[i] + key.charCodeAt(i % key.length)) % 256;
		[s[i], s[j]] = [s[j], s[i]];
	}
	i = 0;
	j = 0;
	for (let y = 0; y < str.length; y++) {
		i = (i + 1) % 256;
		j = (j + s[i]) % 256;
		[s[i], s[j]] = [s[j], s[i]];
		x = str.charCodeAt(y) ^ s[(s[i] + s[j]) % 256];
		res.write(x);
	}
	return Packages.java.util.Base64.getEncoder().encodeToString(res.toByteArray());
}

