function importData(url) {
	try{
		if(!(String(url||"").trim())) {
			url="https://jsonblob.com/api/jsonBlob/1334825983680897024"
		}
		setInfo("æ•°æ®åœ°å€",url)
		saveProgress("\nâœ…å¼€å§‹å¯¼å…¥æ•°æ®")
		sleep(800)
		url = String(url)
		isDelete = false
		if (url.startsWith("http")) {
			path = java.downloadFile(url)
			isDelete = true
		} else if(url.startsWith("/storage")&&(!url.endsWith("zip"))) {
			path = url
		} else {
			m = url.match(/(data\/[^\/]+\/cache)?\/?([^\/]+\.json|[^\/]+\.zip)/)
			path = "/"+m[m.length-1]
		}
		if (path.endsWith("zip")) {
			 zipPath=java.unArchiveFile(path)
			 data=java.getTxtInFolder(zipPath)
		} else {
			data = java.importScript(path)
		}
		data = /base64Data/.test(data)?JSON.parse(data).base64Data:data
		data = /^[A-Za-z0-9+/]+={0,2}$/.test(data)?java.base64Decode(data):data
		data = JSON.parse(data)
		saveProgress('\nâœ…æ•°æ®æ•°ç›®:'+Object.keys(data.books).length)
