exploreUrl:
@js:
var result = [];
var push = (title, url, size) => result.push({
    "title": title,
    "url": url,
    "style": {
        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
    }
});
var baseUrl = "http://www.wodudu.cc"; 
var tag = java.ajax(baseUrl);
if (tag) {
    a = org.jsoup.Jsoup.parse(tag).select(".nav li a");       
    push("全本小说",`/quanben/class/{{page}}/`,0.33);
    push("🔥分类小说🔥", null, 1);
    for (i in a) {
        title = a[i].text();
        url = a[i].attr("href");
        size = 0.33;
        title = String(title).replace(/\s/g, ""); 
        url = baseUrl + String(url).replace(/\/\d+\/$/, "/") + "{{page}}/"; 
        push(title, url, size);
    }
} 
JSON.stringify(result);

ruleBookInfo.init:
@js:
(function(){
    let doc = org.jsoup.Jsoup.parse(result)
    let og = (selector) => doc.select('[property="og:' + selector + '"]').attr('content')
    return {
        name: og('novel:book_name'),
        author: og('novel:author'),
        kind: og('novel:category')
        +'•'+og('novel:status')
      +'•'+String(og('novel:update_time')),
        latest: og('novel:latest_chapter_name'),
        cover: og('image'),
        intro: og('description'),
        url:og('novel:read_url')
    }
})()

