@js:
aList=java.getElements('.chaplist-box@a');
list=[]
for(i in aList){list.push({"title":aList[i].text(),"url":aList[i].attr('href')})}; 
var id = baseUrl.match(/com\/(\w+)/)[1];
option={"method": "POST","body": "id=" +id}
url="https://m.dumanwu.com/morechapter"
ls=JSON.parse(java.ajax(url+','+JSON.stringify(option))).data 
for(i in ls){
url = baseUrl + "/" + ls[i].chapterid + ".html"
list.push({"title":ls[i].chaptername,"url":url})}
list.reverse()

