}

function getUrl(type, data) {
	t = i[type]
	boundary = getUuid(16, 62)
	securityCode =rc4("Zo1Eq4V2mr269K4doL9U4093U25acjMQ",JSON.stringify({
		'uid': getUuid(48, 62),
