jsLib:
function v(key, value, del){
	const {source} = this
	let data = source.variable
	try{data = JSON.parse(data)}catch(e){data = {}}
	if(!!value||value==0)data[key] = value
	if(del)delete data[key]
	source.variable = JSON.stringify(data)
	return data[key]
}
function cf(url, times){
	const {cache, java, cookie} = this
	let f = (n,v,d) => this.v(n,v,d), result, id = (Math.random()+0.1)*10, times = times?times:0, yz = url
	if(/search.php,/.test(yz))yz = completeUrl.call(this, '/',yz)
	try{
		if(!f('ok')&&!f('lock')){
		 f('lock', id)
		 java.log(id)
		 if(f('lock') == id){
		 	result = java.startBrowserAwait(yz,"验证"), e()
		 }
	 }
	}catch{f('ok', 1);f('lock', null, 1)}
	while(f('lock')){}
	if(yz!=url||!result||result.body().match('_cf_')){
		result = java.connect(url)
		if(result.body().match('_cf_')){
			f('ok', null, 1)
			if(times==0)result = cf.call(this, url, times+1)
		}
	}
	return result
}
function fixImg(img, url){
	const {java} = this
	if(!v.call(this, 'f')&&!/\ds\.jpg$/.test(img)){
		let m=url.match(/\/(\d+)(\.[^.]*)?$/)
		if(!m)return img
		img = this.completeUrl('/bimages/'+Math.floor(m[1]/1000)+'/'+m[1]+'/'+m[1]+'s.jpg', url)
	}
	return img
}
function randomUa(){
	let r = (a,b) => Math.floor(Math.random()*(b-a)+a), x = l =>{for(i=0,s='';i<l;i++){s+=r(0,10)}return s}, v = r(500, 700)+'.'+x(2)
	return `Mozilla/5.0 (Linux; Android ${r(5,15)}; wv) AppleWebKit/${v} (KHTML, like Gecko) Chrome/${r(76,122)}.0.${x(4)}.${x(2)} Mobile Safari/${v}`
}
function getDomain(){
	const {source} = this
	let domain = v.call(this, 'Domain')
	if(!domain||domain=='')domain=source.key
	return domain
}
function completeUrl(url, base) {
  let baseUrl = base || getDomain.call(this)
  if(v.call(this,'k'))url = String(url).replace(/.*\/\/[^/]+/, '')
  let m = baseUrl.match(/(.+?)\/\/([^/]+)(.*\/?)(.*)/)
  let fill = ''
  if (url.match(/^https?:/)) {
  } else if (url.match(/^\/\//)) {
    fill = m[1]
  } else {
    fill = m[1] + '//' + m[2]
    if (!url.match(/^\//)){
    	fill = fill + m[3]
    }
  }
  return fill + url
}

