            subResult.push({
                "title": title,
                "url": url,
                "style": {
                    "layout_flexGrow": 1,
                    "layout_flexBasisPercent": size
                }
            }); 
         }
       } 

    if (subResult.length > 0) {
        if (subResult[0].title === "首页") {
            subResult.shift();
        }
    }
    if (subResult.length > 1) {
        subResult.length = subResult.length - 1;
    } else {
        subResult = [];
    }
    result = result.concat(subResult);
} 
JSON.stringify(result);

