@js:
var result = [];
var push = (title, url, size) => result.push({
    "title": title,
    "url": url,
    "style": {
        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
    }
});
var baseUrl = "http://www.cntnn.com"; 
var tag = java.ajax(baseUrl);
if (tag) {
    a = org.jsoup.Jsoup.parse(tag).select(".nav li a");    
    push("🔥排行榜单🔥",null, 1);
    push("全本小说",`/quanben/html/{{page}}/`,0.33);
    push("小说排行",`/rank/`,0.33);
    push("总排行榜",`/rank/allvisit/`,0.33);
    push("月排行榜",`/rank/monthvisit/`,0.33);
    push("周排行榜",`/rank/weekvisit/`,0.33);
    push("总收藏榜",`/rank/goodnum/`,0.33);

    push("🔥分类小说🔥", null, 1);
    for (i in a) {
        title = a[i].text();
        url = a[i].attr("href");
        size = 0.33;
        title = String(title).replace(/\s/g, ""); 
        url = baseUrl + String(url).replace(/\/\d+\/$/, "/") + "{{page}}/"; 
        push(title, url, size);
    }
} 
JSON.stringify(result);

