var tag = java.ajax("https://www.ffxs8.com");
if (tag) {
    var a = org.jsoup.Jsoup.parse(tag).select(".nav li a");
    var subResult = [];
    for (var i in a) {
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.25;
        title = String(title).replace(/\s/g, "");
        url = baseUrl+String(url).replace(/\/$/, "") + "/<,index_{{page}}.html>";
        subResult.push({
            "title": title,
            "url": url,
            "style": {
                "layout_flexGrow": 1,
                "layout_flexBasisPercent": size
            }
        });
    }
    if (subResult.length > 5) {
        subResult = subResult.slice(1, subResult.length);
    } else {
        subResult = [];
    }
    push("🔥分类小说🔥", null, 1);
    result = result.concat(subResult);
} 
JSON.stringify(result);

