@js:
if (result.includes("/photos-item-aid")) {
  var res = java.ajax(baseUrl.replace("list", "item"));
  var page_url = res.match(/"page_url":(\[.*\]),/)[1].replace(/\\|,(?!")/g,"");
  JSON.parse(page_url).map(imgurl=>{
    return "<img src=\"" + imgurl + "\">"
  }).join("\n");
} else {
  eval(result.match(/var fast_img_host=\\"([^"]*)\\";/)[0].replace(/\\/g,""));
  eval(result.match(/var imglist = (\[\{.*\}\]);/)[0].replace(/\\/g,""));
  imglist.filter(img=>{
    if (!img.caption) return img.url;
  }).map(img=>{
    return "<img src=\"" + img.url + "\">"
  }).join("\n");
}

