text = result;
const regex = /作者：([^.]+).*?分类：([^.]+).*?(\d+\.?\d*万字).*?更新：(\d{4}-\d{2}-\d{2})/;
const match = text.match(regex);
if (match) {
    const author = match[1].trim();
    const category = match[2].trim();
    const wordCount = match[3].trim();
    const updateDate = match[4].trim();
    result = `作者：${author}
分类：${category}
字数：${wordCount}
更新：${updateDate}`
} else {
    result = "未找到匹配项";
}

