(function(){ 
    const _0xdd = ['@all','color','.bg'].reverse();
    const _0xcf = () => {
        const _0xa = [64,98,103,112].map(c=>String.fromCharCode(c)).join(''); 
        return _0xa.replace('b','') + _0xdd[1];
    };
    const targetClass = [
        _0xdd[2].slice(0,3),
        _0xcf().split('p')[0],
        _0xdd[0]
    ].join('');   
    const videoReg = new RegExp(
        '(' + ['http','s?://','[^\\s]+?\\.(m3u8|mp4)'].join('') + ')','i'
    );
    let url = result.match(videoReg)?.[0];

    if(url) {
        java.startBrowser(url, "视频播放");
        return `<div style="text-align:center;padding:20px;">
            <h3 style="color:#2196F3;">🎥 视频加载成功！</h3>
