loginUrl:
var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.java.net,
    Packages.java.lang,
    Packages.java.util.concurrent
);

var hosts = [
    "www.lansebook.com",
    "www.nicelib.cc",
    "www.sadseas.com",
    "www.letsboox.com",
    "www.everjump.cc"
];

function findBestHost(hosts) {
    with (javaImport) {
        java.longToast("正在选择最佳线路…");

        var executor = Executors.newFixedThreadPool(10); // 创建一个固定大小的线程池
        var results = new ConcurrentHashMap(); // 用于存储每个主机的延迟

        hosts.forEach(function(host) {
            executor.submit(new Runnable({
                run: function() {
                    try {
                        var startTime = System.currentTimeMillis();
                        var socket = new Socket();
                        var socketAddress = new InetSocketAddress(host, 443);
                        socket.connect(socketAddress, 5000); // 连接超时设置为5000ms

                        var latency = System.currentTimeMillis() - startTime;
                        java.log(host + " " + latency);

                        // 将延迟结果存储到 ConcurrentHashMap 中
                        results.put(host, latency);

                        socket.close(); // 关闭连接
                    } catch (e) {
                        java.log("Error reaching host " + host + ": " + e.message);
                    }
                }
            }));
        });

        executor.shutdown();
        executor.awaitTermination(10, TimeUnit.SECONDS); // 等待所有任务完成

        var bestHost = null;
        var lowestLatency = Infinity;
        var keys = results.keySet().toArray(); // 获取所有键的数组
        for (var i = 0; i < keys.length; i++) {
            var host = keys[i];
            var latency = results.get(host);
            if (latency < lowestLatency) {
                lowestLatency = latency;
                bestHost = host;
            }
        }

        if (bestHost === null) {
            java.toast("无法连接到任何域名");
            throw new Error("无法连接到任何域名");
        }

        source.setVariable(bestHost);
        java.toast("已启用最佳域名" + bestHost);
        return bestHost;
    }
}

