var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.java.net,
    Packages.java.lang,
    Packages.java.util.concurrent
);

var hosts = [
    "www.lansebook.com",
    "www.nicelib.cc",
    "www.sadseas.com",
    "www.letsboox.com",
    "www.everjump.cc"
];

function findBestHost(hosts) {
    with (javaImport) {
        java.longToast("æ­£åœ¨é€‰æ‹©æœ€ä½³çº¿è·¯â€¦");

        var executor = Executors.newFixedThreadPool(10); // åˆ›å»ºä¸€ä¸ªå›ºå®šå¤§å°çš„çº¿ç¨‹æ± 
        var results = new ConcurrentHashMap(); // ç”¨äºå­˜å‚¨æ¯ä¸ªä¸»æœºçš„å»¶è¿Ÿ

        hosts.forEach(function(host) {
            executor.submit(new Runnable({
                run: function() {
                    try {
                        var startTime = System.currentTimeMillis();
                        var socket = new Socket();
                        var socketAddress = new InetSocketAddress(host, 443);
                        socket.connect(socketAddress, 5000); // è¿æ¥è¶…æ—¶è®¾ç½®ä¸º5000ms

                        var latency = System.currentTimeMillis() - startTime;
                        java.log(host + " " + latency);

                        // å°†å»¶è¿Ÿç»“æœå­˜å‚¨åˆ° ConcurrentHashMap ä¸­
                        results.put(host, latency);

                        socket.close(); // å…³é—­è¿æ¥
                    } catch (e) {
                        java.log("Error reaching host " + host + ": " + e.message);
                    }
                }
            }));
        });

        executor.shutdown();
        executor.awaitTermination(10, TimeUnit.SECONDS); // ç­‰å¾…æ‰€æœ‰ä»»åŠ¡å®Œæˆ

        var bestHost = null;
        var lowestLatency = Infinity;
        var keys = results.keySet().toArray(); // è·å–æ‰€æœ‰é”®çš„æ•°ç»„
        for (var i = 0; i < keys.length; i++) {
            var host = keys[i];
            var latency = results.get(host);
            if (latency < lowestLatency) {
                lowestLatency = latency;
                bestHost = host;
            }
        }

        if (bestHost === null) {
            java.toast("æ— æ³•è¿æ¥åˆ°ä»»ä½•åŸŸå");
            throw new Error("æ— æ³•è¿æ¥åˆ°ä»»ä½•åŸŸå");
        }

        source.setVariable(bestHost);
        java.toast("å·²å¯ç”¨æœ€ä½³åŸŸå" + bestHost);
        return bestHost;
    }
}

