@js:
var bookList = [];
var nameCount = {}; 
imglist = java.getElements(".imglist li");
textlist = java.getElements(".textlist li");
for (i = 0; i < imglist.length; i++) {
    item = imglist[i];
    var name = item.select("a span").text();
    if (nameCount[name] !== undefined) {
        nameCount[name]++;
        name += " (" + nameCount[name] + ")"; 
    } else {
        nameCount[name] = 0; 
    }

    bookList.push({
        "name": name,
        "kind": item.select(".tags span a").text(),
        "intr": item.select(".desc").text(),
        "cover": item.select("img").attr("src"),
        "id1": item.select("a").attr("href").match(/(\d+)_\d+_/)[1],
        "id2": item.select("a").attr("href").match(/_(\d+)_/)[1],
        "id3": item.select("a").attr("href").match(/\d+_\d+_(\d+)/)[1]
    });
}
for(i=0; i<textlist.length; i++)
{
	   item = textlist[i];
    var name = item.select("a span").text();
    if (nameCount[name] !== undefined) {
        nameCount[name]++;
        name += " (" + nameCount[name] + ")"; 
    } else {
        nameCount[name] = 0; 
    }
  bookList.push({
    "name": name,
    "kind": "",
    "intr": "",
    "cover": "",
    "id1": "",
    "id2": "",
