ruleSearch.bookList:
$.items[:10]
<js>
let page = java.get("page");
let key = java.get("key");
let authorUrl = `https://app.jjwxc.org/androidapi/search?versionCode=191&keyword=${key}&type=2&page=${page}&searchType=7&sortMode=DESC`;
const authorlist = JSON.parse(java.ajax(authorUrl))?.items;

let list = authorlist?result.toArray().concat(authorlist):result;
let json = [];
for(i in list){
    try{
    	   let item = list[i];
        let id = item?.novelid;
        let url = `https://www.jjwxc.net/onebook.php?novelid=${id}`;     
        let html = String(java.ajax(url));
        java.setContent(html);
        let J = org.jsoup.Jsoup.parse(html);
        let authorid = J.select('h1+span>a, h2 a').attr("href").match(/(\d+)/)[1];
        let des = String(J.select("span[style='color:#F98C4D']").text())
            .replace(/立意：/, '◎ ')
            .replace(/一句话简介：/, '◎ ') + "◎" + java.getElement("id.novelintro").text();
        title = J.select('h1').text();
        let author = J.select('span[itemprop="author"]').text();
        let cover = J.select(".noveldefaultimage").attr("src");

        if (/(?:postimg|bmp|alicdn)\./.test(cover)) {
            cover = `https://i9-static.jjwxc.net/novelimage.php?novelid=${id}`;
        }

        let header = {
            headers: {
                referer: cover.match(/(^https?:\/\/.*?\/)/)[1]
            }
        };
        cover = `${cover},${JSON.stringify(header)}`.replace(/wx\d+/, 'wx2');

        let cat = String(J.select('a[style="text-decoration:none;color: red;"]').text()).replace(/\s/g, ',');
        let status = String(J.select('span[itemprop="updataStatus"]').text());

        let size = String(J.select('span[itemprop="wordCount"]').text()).replace(/字/, '');
        let n = String(java.getElement('@@id.oneboolt@tag.tr.-2@span').text())
            .replace(/\n/, '•')
            .replace(/\*最新更新/, '')
            .replace(/(.*?)\[(?:VIP|福利番外)\]/, '🔒$1');
        let num = java.getElement('@@id.oneboolt@tag.tr.-2@tag.td.0').text();
        let genre = J.select('[itemprop="genre"]').text().split('-')[1];
        let view = html.match(/<span>作品视角：<\/span>([^<]+)<\/li>/)[1].replace(/\s/g, '');

        json.push({
            title,
            author,
            cat: `${view},${status},${genre},${cat}`,
            size,
            url: `https://app-cdn.jjwxc.net/androidapi/novelbasicinfo?novelId=${id}`,
            des,
            new: `${num}、${n}`.replace(/(\d+、)🔒/, '🔒$1').replace(/^章节、|、$/, ''),
            cover
        });
    } catch (e) {
        java.log("搜索列表的错误："+e);
    }
}
JSON.stringify(json)
</js>
