@js:
let baseUrlPattern = /h=(.+?)&/;
let bookIdPattern = /bookid=(\d+?)&/;
let matchPattern = /1-\\\/-(\d+)-\\\/-(.*?)[:-]/g;
const SIGN_KEY = 'stvmobilereader&sajax=readchapter&erogh982^%*%^*';
let cookieHeader = 'transmode=chinese;' + source.getLoginHeaderMap().cookie;

let hMatch = baseUrl.match(baseUrlPattern);
let bookIdMatch = baseUrl.match(bookIdPattern);

if (!hMatch || !bookIdMatch) {
  throw new Error("没有获取到书籍信息");
}

let h = hMatch[1];
let bid = bookIdMatch[1];

let resultArray = [];

result.replace(matchPattern, (match, urlIndex, name) => {
  let sign = java.md5Encode(`bookid=${bid}&c=${urlIndex}&h=${h}&key=${SIGN_KEY}`);
  let headers = JSON.stringify({
    headers: {
      cookie: cookieHeader,
      'X-STV-Sign': sign
    }
  });
  let url = `https://sangtacviet.vip/index.php?sajax=readchapter&h=${h}&bookid=${bid}&c=${urlIndex}&key=stvmobilereader,${headers}`;
  resultArray.push({
    name,
    url
  });
  return match;
});

resultArray;

