)

function replace (content) {
  let source = "成阴唇淫洞根经交叫媒媚女肉乳物性穴血欲嘴虐童茎舔棍蜜兽液肉喉鸡巴奸尻慰熟殖潮感肛酥胸插验龟蒂射精颜缩爆菊幼恋伦轮缝密处肥操臀膜跨蹂躏洛鸦妓楼凤胴戳奶逼诱蒲荡骚偷露裸漏御嫩惑陰莖獸雞龜陽顏縮戀屍姦亂術輪遺縫處躪鴉鳳侶誘蕩騒禦屄屌脫扒光膚裙她撫美豐腴揉撩粉腿胯禁摸享受褲襠内具亢奮伸入貼緊股動情擠身壓羞澀嬌顫勃褻瀆衣硬癢體碰觸色痞器官渴望粗摩擦挺呻吟豊絲襪肌捏搓柔軟隱秘毛少溝汁愛透濕滑抽饑餓喘"
  let replacementChars = source.split("")

  return Array.from(content, char => {
    let code = char.charCodeAt(0)
    if (code >= 0x0900 && code <= 0x09C2) {
      return replacementChars[code - 0x0900]
    } else {
      return char
    }
  }).join('')
}
