nk = "";

function Sort_() {
	const { source } = this
var	sgv = JSON.parse(source.getVariable());
var sorts = [
"/order/update_time+desc",
"/order/hits+desc",
"/order/word+desc"
];
var sort = sgv.s ? sorts[sgv.s - 1] : "";
return sort;
}

function GetUL() {
	const { source } = this
	let sourceul = JSON.parse(source.getVariable()).sourl;
	return sourceul;
}

//计算最终value值
function getval(kk) {
	   const { java } = this
    var val = "";
    for (var i = 0; i <= kk.length - 1; i++) {
        var str = kk.charAt(i);
        var code = str.charCodeAt();
        val += parseInt(code) + 1;
    }
    valu = java.md5Encode(val);
    return valu;
}

function timeov(times) {
    const date = new Date(times);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const seconds = String(date.getSeconds()).padStart(2, '0');

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
}

//自定义书架
function booklist() {
	const { java , source } = this
 let all = JSON.parse(source.getVariable()).booklist;
 let list = String(JSON.stringify(all));
 let hm = ""
 let cc = java.base64Decode(hm);
 let html = String(cc).replace(/◎参考标识◎/,`const list = ${list}`);
 //java.log(html);
return java.base64Encode(html);;
}

