&nbsp;&nbsp;📖书籍ID：{{baseUrl.match(/novel\/(\d+)/)[1]}}
☑当前站源：{{GetUL()}}
🏷️标签：@get:{k}
📜简介：@get:{o}
<js>
const Bookid = baseUrl.match(/novel\/(\d+)/)[1];
const contul = `${GetUL()}/index.php?s=/Home/comment/lists/id/${Bookid}/type/novel/limit/5/size/12`
var CommentNur = JSON.parse(source.getVariable()).co;
var cont = "";
if (!CommentNur) {
    var src = java.ajax(contul);
    java.setContent(src);
    var list = java.getElement('//script[@src="/public/static/jquery/jquery.min.js"]/following-sibling::script[1]');
    var lists = JSON.parse(String(list[0]).match(/var\s*trre=([\s\S]+),page_num/)[1]);
    //java.log(JSON.stringify(lists,null,"\t"));

    cont += "🔻评论区：\n❗️右上角书籍变量填入评论后刷新详情页发送评论。❗️\n";
    if (lists.length > 0) {
        if (source.getLoginHeader()) {
            let jc = source.getLoginHeaderMap();
            let inf = decodeURIComponent(jc.get("Cookie"));
            let info = decodeURIComponent(inf).match(/\{[\s\S]+\}/)[0];
            let myuid = JSON.parse(info)['uid'];
            //处理我的评论数据
            var myComments = [];
            function collectMyComments(comment, parentPath) {
                let floor = parentPath ? `${parentPath}-${comment.floorIndex}` : comment.floorIndex;
                if (comment.uid === Number(myuid)) {
                    myComments.push({
                        id: comment.id,
                        username: comment.user.username,
                        group: comment.user.group,
                        floor: `【${floor}楼】`,
                        time: timeov(comment.create_time * 1000),
                        content: comment.content
                    });
                }
                if (comment._?.forEach) {
                    comment._.forEach((child, index) => {
                        child.floorIndex = index + 1;
                        collectMyComments(child, floor);
                    });
                }
            }
            lists.forEach((c, i) => {
                c.floorIndex = i + 1;
                collectMyComments(c, "");
            });
            //java.log(JSON.stringify(myComments));
            var mySection = "=== 我的评论（共找到" + myComments.length + "条） ===\n\n";
            myComments.forEach(m => {
                mySection += `┌【ID：${m.id}】| 👤${m.username} | ${m.group} | ${m.time}\n└评论：${m.content}\n${m.floor}\n `;
            });
        } else {
            mySection = "";
        }

        //处理全部评论数据
        function buildComments(comment, parentPath, level) {
            let floor = parentPath ? `${parentPath}-${comment.floorIndex}` : comment.floorIndex;
            let indent = '  '.repeat(level);
            let str = `${indent}┌【ID：${comment.id}】|👤${comment.user?.username || ""} | ${comment.user?.group || "已注销用户"} | ${timeov(comment.create_time * 1000)}\n` +
                `${indent}└评论：${comment.content}\n` +
                `${indent}【${floor}楼】\n\n`;

            if (comment._?.forEach) {
                comment._.forEach((child, index) => {
                    child.floorIndex = index + 1;
                    str += buildComments(child, floor, level + 1);
                });
            }
            return str;
        }

        var allSection = "=== 全部评论（共" + lists.length + "条） ===\n\n";
        lists.forEach((c, i) => {
            c.floorIndex = i + 1;
            allSection += buildComments(c, "", 0);
        });

        //合并评论数据
        cont += mySection + "\n" + allSection;
    } else {
        cont += "本书暂时没有评论喔";
    }
}
result = result + "\n" + cont;
result;
</js>

ruleContent.content:
@js:
var url = GetUL();
//处理强制登录
var nn = String(cookie.getCookie(url));
var coks = nn.split('; '); 
var ckk = coks.filter(item => !item 
.startsWith("lf_reader_num")).join('; ');
cookie.setCookie(url,ckk);
var chapterul = java.hexDecodeToString(src);


java.log("当前阅读进度"+book.durChapterIndex+"|已下载进度"+chapter.index);


//自动处理验证
function content() {
    let text;
    let html = java.ajax(chapterul);
    if (html.includes("滑动验证")) {
        let headers = {
            "User-Agent": "Mozilla/5.0 (Linux; Android 9) Mobile Safari/537.36",
            "Referer": chapterul
        };
        try {
            let s = html.match(/src=\"(\/huadong[^"]+)/)[1];
            let sr = java.get(url + s, headers).body()
            eval(sr.match(/(key=\".+?,value=\"[^;]+?\;)/)[1]);
            let va = getval(value);
            let codeul = url + sr.match(/([^"]+yanzheng_huadong\.php[^"]+)/)[1] + `${key}&value=${va}`;
            let ck = java.get(codeul, headers);
            java.log("验证Cookie：" + JSON.stringify(ck.cookies()));
            java.toast("检测到验证,尝试自动处理……");
            let tx = java.ajax(chapterul);
            text = java.getString('#chapterContent@html', tx);
        } catch(error) {
            java.log(error);
            text = "自动验证失败，请重试!";
        }
    } else {
        text = java.getString('#chapterContent@html', html);
    }
    return text;
}

let text;
if ( Math.abs(chapter.index - book.durChapterIndex) <= 3) {
    text = content();
} else {
	   text = "因网站速率限制，当前预下载超过三章，为避免被封ip，被动停止解析正文。\n建议调小预下载，保持正常阅读速度，刷新即可重新获取";
}

//处理重新获取文本时可能会出现的错误。
let retryCount = 0;
const maxRetries = 3;
let result;
while (retryCount < maxRetries) {
    if (text != false && text !== "") {
        result = text;
        break;
    } else {
    	    retryCount++;
        java.toast(`未获取到文本，正在重试...（当前第${retryCount}次重试）`);
        text = content();
    }
}
if (retryCount === maxRetries) {
    result = "重试三次仍未获取到文本，请检查网络状况。"
}
result;

