var baseUrl = "https://m.huaidanxs.com/category.html";
var tag = java.ajax(baseUrl);
if (tag) {
    var a = org.jsoup.Jsoup.parse(tag).select(".sort a");
    push("🔥分类小说🔥", null, 1);
    for (var i in a) {
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.33;
        title = String(title).replace(/\s/g, "");
        url = String(url)+ "?page={{page}}";      
        push(title, url, size);
  }

