@js:
let source =
    "!\"#$%&'()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_`abcdefghijklmnopqrstuvwxyz";
let replacementChars = source.split("");
let ranges = [
    { start: 0x10a0, end: 0x10f9 },
    { start: 0x30a0, end: 0x30f9 },
    { start: 0x1b80, end: 0x1bd9 }
];
let replaceRegex = /[\u10A0-\u10F9\u30A0-\u30F9\u1B80-\u1BD9]/g;

function replace(content) {
    return content.replace(replaceRegex, function (char) {
        const code = char.charCodeAt(0);
        for (var i = 0; i < ranges.length; i++) {
            var range = ranges[i];
            if (code >= range.start && code <= range.end) {
                return replacementChars[code - range.start];
            }
        }
    });
}
replace(result);

