function parseKind(txt) {
const kindClass = {"Doujinshi":"同人志","Manga":"漫画", "Artist CG":"画师CG","Game CG":"游戏CG","Non-H":"无H","Image Set":"图集","Western":"西方","Cosplay":"Cosplay", "Misc":"杂项","Asian Porn":"亚洲色情","Private":"私有"};
txt[2] = txt[2].replace('pages','页');
txt[0] = kindClass[txt[0]];
return txt}

function parseTag(txt) {
const { java, source, cache } = this;
let kk = cache.getFromMemory('tagMap');
if(kk==null){
	let l = source.getVariable();
