  const lines = txt.split('\n');
  const obj = {};

  lines.forEach(line => {
    let [category, tag] = line.split(':');
    if (!obj[category]) {
      obj[category] = [];
    }
    obj[category].push(tag.replace(/_/g, ' '));
  });

  return Object.entries(obj)
    .map(([category, tags]) => {
      const row = rows[category] || category;
      const pTags = tags.map(tag => e2s(tag)+'('+tag+')').join(', ');
      return `${row}: ${pTags}`;
    })
    .join('\n');
