loginUrl:
function login(b){
	if(b==undefined){
		info = JSON.parse(source.getLoginInfo())
	}else{
		source.putLoginInfo(JSON.stringify(result))
		info=result
	}

	so=source.key
	pwd=java.md5Encode(info.密码)
	imgurl=so+'/code.jpg?'+Math.random()
	code=java.getVerificationCode(imgurl)
 if(b){
		url=so+'/qs_login_go.php,'
		body=`user_name=${info.用户名}&user_pass=${pwd}&user_code=${code}`}
	else{
		url=so+'/qs_register_go.php,'
		body=`name=${info.用户名}&mobile=${info.手机号}&pass=${pwd}&pass2=${pwd}&code=${code}`
		}
	post=JSON.stringify({
  "body": String(body),
  "method": "POST"
})
 url+=post
 result=java.ajax(url)
 java.toast(result)
 c=cookie.getCookie(source.key)
 source.setVariable(c)
	}

